(function executeRule(current, previous /*null when async*/ ) {

    // Add your code here

	try {
        var r = new sn_ws.RESTMessageV2('x_70281_covid_trac.Covid19 API', 'GET Request');
        r.setStringParameterNoEscape('status', current.status);

		if(current.country.getDisplayValue() == 'United States of America'){
				r.setStringParameterNoEscape('country', current.country.iso3166_2.getDisplayValue());
			} else
			{
			r.setStringParameterNoEscape('country', current.country.getDisplayValue());
			}

        //override authentication profile 
        //authentication type ='basic'/ 'oauth2'
        //r.setAuthenticationProfile(authentication type, profile name);

