        //by setting a MID server or calling executeAsync, one needs to set skip_sensor
        //to true. Otherwise, one may get an intermittent error that the response body is null
        //r.setEccParameter('skip_sensor', true);

        var response = r.execute();
		var responseBody = response.getBody();
        var httpStatus = response.getStatusCode();

		//gs.info(responseBody);

 		current.status_code = httpStatus;
 		current.response_body = responseBody;

		var a = JSON.parse(responseBody);

		if (a[0].confirmed){
				current.total_number = a[0].confirmed;
				gs.info("Confirmed Cases: " + a[0].confirmed);
		} else if (a[0].death){
				current.death_case = a[0].death;
				gs.info("Death: " + a[0].death);

		} else if (a[0].recovered){
				current.recovered_case = a[0].recovered;
				gs.info("Recovered: " + a[0].recovered);

				   }


// 		var apple = JSON.parse(responseBody);
